begin
s:=s||'SELECT ''Tempo de atendimento '' AS descricao'||unistr('\000a')||
'      ,abs(round(to_number(SUM((x.dt_fecha - x.dt_abre) * 24 * 60)), 0)) tempo_min'||unistr('\000a')||
'      ,abs(round(to_number(SUM((x.dt_fecha - x.dt_abre) * 24)), 2)) tempo_horas'||unistr('\000a')||
'      ,abs(round(to_number(SUM((x.dt_fecha - x.dt_abre))), 2)) tempo_dias'||unistr('\000a')||
'      ,2 ord'||unistr('\000a')||
'  FROM joseluis.cliente_os_evento x'||unistr('\000a')||
' WHERE x.id_cliente = :p25_id_cliente'||unistr('\000a')||
'   AND x.id_os = :p25_id_os HAVI';

s:=s||'NG SUM((x.dt_fecha - x.dt_abre) * 24 * 60) > 0'||unistr('\000a')||
'UNION ALL'||unistr('\000a')||
'SELECT ''Tempo para iniciar atendimento'' AS descricao'||unistr('\000a')||
'      ,abs(round(y.tempo * 24 * 60, 0)) tempo_min'||unistr('\000a')||
'      ,abs(round(y.tempo * 24, 2)) tempo_horas'||unistr('\000a')||
'      ,abs(round(y.tempo, 2)) tempo_dias'||unistr('\000a')||
'      ,1 ord'||unistr('\000a')||
'  FROM (SELECT to_number((x.dt_abre - (SELECT a.dt_abre'||unistr('\000a')||
'                                         FROM joseluis.cliente_os_evento a'||unistr('\000a')||
'        ';

s:=s||'                                WHERE a.id_cliente = x.id_cliente'||unistr('\000a')||
'                                          AND a.id_os = x.id_os'||unistr('\000a')||
'                                          AND a.sq_evento IN (SELECT MIN(sq_evento)'||unistr('\000a')||
'                                                                FROM joseluis.cliente_os_evento'||unistr('\000a')||
'                                                               WHERE id_cliente = x.id_cli';

s:=s||'ente'||unistr('\000a')||
'                                                                 AND id_os = x.id_os'||unistr('\000a')||
'                                                                 AND tp_evento = ''AAT'')))) tempo'||unistr('\000a')||
