'         WHERE x.id_cliente = :p25_id_cliente'||unistr('\000a')||
'           AND x.id_os = :p25_id_os'||unistr('\000a')||
'           AND x.tp_evento = ''AOS'') y'||unistr('\000a')||
'UNION ALL'||unistr('\000a')||
'SELECT ''Tempo para finalizar atendiment';

s:=s||'o'''||unistr('\000a')||
'      ,abs(round(y.tempo * 24 * 60, 0))'||unistr('\000a')||
'      ,abs(round(y.tempo * 24, 2))'||unistr('\000a')||
'      ,abs(round(y.tempo, 2))'||unistr('\000a')||
'      ,3 ord'||unistr('\000a')||
'  FROM (SELECT to_number((x.dt_abre - (SELECT a.dt_abre'||unistr('\000a')||
'                                         FROM joseluis.cliente_os_evento a'||unistr('\000a')||
'                                        WHERE a.id_cliente = x.id_cliente'||unistr('\000a')||
'                                          AND a.id_os = x.id_os'||unistr('\000a')||
'          ';

s:=s||'                                AND a.sq_evento IN (SELECT MAX(sq_evento)'||unistr('\000a')||
'                                                                FROM joseluis.cliente_os_evento'||unistr('\000a')||
'                                                               WHERE id_cliente = x.id_cliente'||unistr('\000a')||
'                                                                 AND id_os = x.id_os'||unistr('\000a')||
'                                                  ';

s:=s||'               AND tp_evento = ''FIA'')))) tempo'||unistr('\000a')||
'          FROM joseluis.cliente_os_evento x'||unistr('\000a')||
'         WHERE x.id_cliente = :p25_id_cliente'||unistr('\000a')||
'           AND x.id_os = :p25_id_os'||unistr('\000a')||
